#define PLAYER_ERROR(a) printf(a)
#define PLAYER_ERROR1(a, b) printf(a, b)
#define PLAYER_ERROR2(a, b, c) printf(a, b, c)
#define PLAYER_ERROR3(a, b, c, d) printf(a, b, c, d)
#define PLAYER_WARN(a) printf(a)
#define PLAYER_WARN1(a, b) printf(a, b)
#define PLAYER_MSG0(x, a) printf(a "\n");
#define PLAYER_MSG1(x, a, b) printf(a "\n", b)
#define PLAYER_MSG2(x, a, b, c) printf(a "\n", b, c)
#define PLAYER_MSG3(x, a, b, c, d) printf(a "\n", b, c, d)
#define PLAYER_MSG4(x, a, b, c, d, e) printf(a "\n", b, c, d, e)
#define PLAYER_MSG5(x, a, b, c, d, e, f) printf(a "\n", b, c, d, e, f)
#define PLAYER_MSG6(x, a, b, c, d, e, f, g) printf(a "\n", b, c, d, e, f, g)
